mapper.select:
select
u.user_id, u.dept_id, u.nick_name, u.user_name, u.email, u.user_type, u.avatar, u.phonenumber, u.sex, u.status, u.del_flag,
u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, u.party_member_id, u.ethnicity, u.birth_date, u.education,
u.category, u.application_date, u.activist_date, u.development_object_date, u.probationary_member_date, u.full_member_date,
u.is_missing, u.is_mobile, u.image_path,
d.dept_name, d.leader
from sys_user u
left join sys_dept d on u.dept_id = d.dept_id
where u.del_flag = '0'

AND u.user_id = #{userId}


AND u.user_name like concat('%', #{userName}, '%')


AND u.status = #{status}


AND u.user_type = #{userType}


AND u.phonenumber like concat('%', #{phonenumber}, '%')


AND u.party_member_id like concat('%', #{partyMemberId}, '%')


AND u.ethnicity = #{ethnicity}


AND u.education = #{education}


AND u.category = #{category}


AND u.is_missing = #{isMissing}


AND u.is_mobile = #{isMobile}


AND date_format(u.create_time,'%Y%m%d') >= date_format(#{params.beginTime},'%Y%m%d')


AND date_format(u.create_time,'%Y%m%d') <= date_format(#{params.endTime},'%Y%m%d')


AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))

${params.dataScope}

select distinct u.user_id,u.dept_id,u.user_name,u.nick_name,u.email,u.phonenumber,u.status,u.create_time,u.party_member_id,u.ethnicity,u.birth_date,u.education, u.category,u.application_date,u.activist_date,u.development_object_date,u.probationary_member_date,u.full_member_date,u.user_type, u.is_missing,u.is_mobile,u.image_path from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

SELECT ur.user_id as userId, r.role_id as roleId, r.role_name as roleName, r.role_key as roleKey, r.role_sort as roleSort FROM sys_user_role ur INNER JOIN sys_role r ON ur.role_id = r.role_id WHERE ur.user_id IN
#{userId}
AND r.del_flag = '0' ORDER BY ur.user_id, r.role_sort

SELECT up.user_id as userId, p.post_id as postId, p.post_name as postName, p.post_code as postCode, p.post_sort as postSort FROM sys_user_post up INNER JOIN sys_post p ON up.post_id = p.post_id WHERE up.user_id IN
#{userId}
AND p.del_flag = '0' ORDER BY up.user_id, p.post_sort

SELECT u.user_id, u.dept_id, u.nick_name, u.user_name, u.email, u.user_type, u.avatar, u.phonenumber, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, u.party_member_id, u.ethnicity, u.birth_date, u.education, u.category, u.application_date, u.activist_date, u.development_object_date, u.probationary_member_date, u.full_member_date, u.is_missing, u.is_mobile, u.image_path, d.dept_name, d.leader, d.dept_img, GROUP_CONCAT(DISTINCT r.role_name SEPARATOR ',') as party_role, GROUP_CONCAT(DISTINCT p.post_name SEPARATOR ',') as party_post FROM sys_user u LEFT JOIN sys_dept d ON u.dept_id = d.dept_id LEFT JOIN sys_user_role ur ON u.user_id = ur.user_id LEFT JOIN sys_role r ON ur.role_id = r.role_id AND r.del_flag = '0' LEFT JOIN sys_user_post up ON u.user_id = up.user_id LEFT JOIN sys_post p ON up.post_id = p.post_id AND p.status = '0' WHERE u.del_flag = '0'
AND u.user_id = #{userId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.status = #{status}
AND u.user_type = #{userType}
AND u.phonenumber like concat('%', #{phonenumber}, '%')
AND u.party_member_id like concat('%', #{partyMemberId}, '%')
AND u.ethnicity = #{ethnicity}
AND u.education = #{education}
AND u.category = #{category}
AND u.is_missing = #{isMissing}
AND u.is_mobile = #{isMobile}
AND date_format(u.create_time,'%Y%m%d') >= date_format(#{params.beginTime},'%Y%m%d')
AND date_format(u.create_time,'%Y%m%d') <= date_format(#{params.endTime},'%Y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))
${params.dataScope} GROUP BY u.user_id ORDER BY u.user_id ASC

mapper.update:
update sys_user
dept_id = #{deptId}, user_name = #{userName}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, user_type = #{userType}, update_by = #{updateBy}, remark = #{remark}, party_member_id = #{partyMemberId}, ethnicity = #{ethnicity}, birth_date = #{birthDate}, education = #{education}, category = #{category}, application_date = #{applicationDate}, activist_date = #{activistDate}, development_object_date = #{developmentObjectDate}, probationary_member_date = #{probationaryMemberDate}, full_member_date = #{fullMemberDate}, is_missing = #{isMissing}, is_mobile = #{isMobile}, image_path = #{imagePath}, update_time = sysdate()
where user_id = #{userId}
